
public class Question4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//TimesTables
		//Print a multiplication table for numbers up to 12
		
		int a;
		int b;
		
		for(a = 1; a <= 12; ++a) {
			for(b = 1; b <= 12; ++b) {
			
				System.out.println(a + " x " + b);
	        }
	        
	    }
	}
}